import React from "react";
import {useEffect} from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";



const Card = ({ name, username, id }) => {
  
  const {favourites, setFavourites} = useGlobalStates()
  const {theme, changeTheme} = useGlobalStates()

  useEffect(() => {
    localStorage.setItem('favs', JSON.stringify(favourites))
  }, [favourites])
  
  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    const card = {
      id: id,
      name: name,
      username: username
    }
    if(favorites.some(fav => fav.id === card.id)){
      alert('It has been add to favs')
    }else{
      setFavourites([...favourites, card])
      alert('Add favs')
    }


  }
  return (
    <div className="card" >
      <img src="./images/doctor.jpg" alt="doc" width='200px'/>
      
      <p>{<Link to={`/${id}`} className="dentistName" id={theme.textdark}>{name}</Link>}</p>
      <p className="user-dentist" id={theme.textwhite}>{username} </p>
      

        {/* En cada card deberan mostrar en name - username y el id */}
        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}
        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton" id={theme.buttondark}>Add Fav</button>
    </div>
  );
};

export default Card;
