import React from 'react'
import { Link } from "react-router-dom";
import { useContext } from "react";
import { useContextGlobal } from "./utils/ContextGlobal";
import { initialState } from './utils/global.context';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {
  const { theme, setDarkTheme, setLightTheme } = useContextGlobal(initialState);
  const isDarkMode = theme === "dark" || false;
  const changeTheme = () => { if (isDarkMode) { setLightTheme(); } else { setDarkTheme(); }};

  return (
    <nav className="nav" id={theme.theme}>
      {/* Aqui deberan agregar los liks correspondientes a las rutas definidas */}
      {/* Deberan implementar ademas la logica para cambiar de Theme con el button */}
      <div className="links-navbar">
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/dentist/:id">Dentista</Link></li>
          <li><Link to="/contact">Contacto</Link></li>
          <li><Link to="/favs">Favoritos</Link></li>
        </ul>
      </div>
      <button className={`changeTheme${isDarkMode ? "light" : "dark"}`} onClick={() => dispatch({ type: "theme" })}>Change theme</button>
    </nav>
  )
}

export default Navbar
