import React from "react";
import {useEffect} from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";



const Card = ({ dentist}) => {
  
  const {dentists} = useContextGlobal()
  const{theme, changeTheme}= useContextGlobal()

  useEffect(() => {
    localStorage.setItem('favs', JSON.stringify(dentists))
  }, [dentists]);
  
  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    const favourites = JSON.parse(localStorage.getItem("favs") || "[]");
    const dentist = {
      id: dentist.id,
      name: dentist.name,
      username: dentist.username,
    };
    dentist.push(favourites);
    localStorage.setItem("favs", JSON.stringify(favourites));
    alert("The dentist it has been added to favs")
  };

  return (
    <div className={theme.className}>
        <Link to={"/detail/" + dentist.id}>
          <h4>{dentist.name}</h4>
        </Link>
        <img className="doctor" src="../images/doctor.jpg" alt="" />
        <h5>{dentist.username}</h5>
        <p>ID {dentist.id}</p>
      
        {/* En cada card deberan mostrar en name - username y el id */}
        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}
        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton" id={changeTheme}>Add Fav</button>
    </div>
  );
};

export default Card;
