import { useContext } from "react";
import { createContext, useState, useMemo, memo} from "react";
import { useEffect } from "react";
import axios from "axios";


export const initialState = {themes:{
  light:{
    className:"LIGHT"
  },
  dark:{
    className:"DARK"
  }
}};

export const ContextGlobal = createContext(undefined);
 

 const ContextProvider = ({ children }) => {
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo
  const [dentist, setDentist] = useState([])
  const [theme, setTheme] = useState(initialState.themes.light)
  const params = useMemo(() => ({ theme }), [theme]);
  const changeTheme = () => {
    setTheme(theme === 'LIGHT' ? 'DARK' : 'LIGHT');
    };
  const MemoChangeTheme = memo(changeTheme);

  useEffect(() => {
    axios.get("https://jsonplaceholder.typicode.com/users")
  .then(res => setDentist(res.data))
  .catch(err => console.log(err))
  }, [])
  
  

  return (
    <ContextGlobal.Provider value={{dentist, theme, setTheme, changeTheme}}>
      <MemoChangeTheme params={params} />
      {children}
    </ContextGlobal.Provider>
  );
};
export default ContextProvider

export const useContextGlobal = ()=>{
  return useContext(ContextGlobal)
}
