import React from "react";
import { useState } from "react";
import Contact from "../Routes/Contact";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  const [values, setValues] = useState({
    fullname: "",
    email: "",
  })

  const [showError, setShowError] = useState(false);
	const [showSubmited, setShowSubmited] = useState(false);

  const [validation, setValidation] = useState(false)

  function validateEmail(email) {
		const emailRegex = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/;
		return emailRegex.test(email);
	}

	function validateName(name) {
		const nameRegex = /^[a-zA-Z ]{6,}$/;
		return nameRegex.test(name);
	}

	function handleSubmit(e) {
		e.preventDefault();
		if(!validateEmail(contact.email) || !validateName(contact.fullName)) {
			setShowError(true);
		} else {
			setShowSubmited(true);
		}
	}

  const handleChange = (e) => {
    setContact({
			...contact,
			[e.target.name]: e.target.value,
		})
    }


  function handleOnFocus() {
		setShowError(false);
		setShowSubmited(false);
	}


  return (
    <div>
      <form>
      <label htmlFor="name">Name</label>
        <input type="text" name="name" className="input-form" onFocus={handleOnFocus} onChange={handleChange} />
        <label htmlFor="email">Email</label>
        <input type="text" name="email" className="input-form" onFocus={handleOnFocus} onChange={handleChange} />
        <button type="submit" className="btn-form">SUBMIT</button>
      </form>
      {showError && <p>Verify introduced info.</p>}
      {showSubmited && <p> {contact.fullName}, thank you for your aplication. We will contact you as soon as possible</p>}
    </div>
  );
};

export default Form;
