import React from "react";
import { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";

const handleChangeTheme = () => {
  theme === themes.dark ? setTheme(themes.light) : setTheme(themes.dark);
};

export const getFavStorage = () => {
  const localData = localStorage.getItem("favs");
  return localData ? JSON.parse(localData) : [];
};


const setFavStorage = (dentist) => {
  const StorageFav = getFavStorage();
  const ListFav = StorageFav.filter((fav) => {
    return fav.id === dentist.id;
  });
  if (ListFav.length === 0) {
    StorageFav.push(dentist);
    localStorage.setItem("favs", JSON.stringify(StorageFav));
    alert("The dentist has been correctly update");
  } else {
    alert("The dentist has been already set as favourite");
  }
};


const removeFavStorage = (id) => {
  const StorageFav = getFavStorage();
  const index = StorageFav.findIndex((fav) => fav.id === id);
  if (index !== -1) {
    StorageFav.splice(index, 1);
    localStorage.setItem("favs", JSON.stringify(StorageFav));
    alert("The dentist has been successfully deleted");
  } else {
    alert("An error has occurred");
  }
};


const Card = ({ name, username, id }) => {


  const [active, setActive] = useState(false);

  useEffect(() => {
    if (StorageFav.includes(dentist.id)) {
      setActive(true)
    }
  },[StorageFav, dentist.id])
  

  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    setFavStorage({ name, username, id });
  }

  const favourite = isFav(dentist.id);

  return (
    <div className="card">
      <img src="/images/doctor.jpg" alt="doctor placeholder" />
      <Link to={`/dentist/${id}`}>
        <h5>{name}</h5>
      </Link>
      <p>{username}</p>        
        {/* En cada card deberan mostrar en name - username y el id */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
