import React from "react";
import { useState } from "react";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  const [values, setValues] = useState({
    fullname: "",
    email: "",
  })

  const [showError, setShowError] = useState(false);
	const [showSubmited, setShowSubmited] = useState(false);

  const [validation, setValidation] = useState(false)

  const validateName = () => {

    let nameUser = values.name.split(" ")
    if (nameUser.length < 5) return alert("Please, enter your full name")
    return true
  }

  const validateEmail = () => {

    let emailUser = values.email.split("@")
    if (emailUser.length < 5) return alert("Wrong email format! It has to conatin an @ and a dot com extention")

    let dotCom = emailUser[1].split(".")
    if (dotCom.length < 5) return alert("Wrong email format! It has to conatin an @ and a dot com extention")

    return true
  }

  const handleChange = (e) => {

    const { target } = e
    const { name, value } = target

    const newValues = {
      ...values,
      [name]:value,
    }

    setValues(newValues)

  }

  const handleSubmit = (e) => {

    e.preventDefault()

    let nameValidation = validateName()

    let emailValidation = validateEmail()

    if (nameValidation && emailValidation) {
      setValidation(true)
    }

  }

  function handleOnFocus() {
		setShowError(false);
		setShowSubmited(false);
	}


  return (
    <div>
      <form>
      <label htmlFor="name">Name</label>
        <input type="text" name="name" className="input-form" onFocus={handleOnFocus} onChange={handleChange} />
        <label htmlFor="email">Email</label>
        <input type="text" name="email" className="input-form" onFocus={handleOnFocus} onChange={handleChange} />
        <button type="submit" className="btn-form">SUBMIT</button>
      </form>
      {validation ? <h3>Thank you for your aplication {values.name}, we will contact you as soon as possible</h3> : ""}
    </div>
  );
};

export default Form;
