import * as React from 'react';
import { Link } from "react-router-dom";
import { useContextGlobal } from ".Components/utils/global.context";


const Card = ({ dent }) => {

 const{theme}= useContextGlobal()
  const addFav = () => {
    // Aqui iria la logica para agregar la Card en el localStorage

    const dentists = JSON.parse(localStorage.getItem("dentists") || "[]");
    const dentist = {
      id: dent.id,
      name: dent.name,
      username: dent.username,
    };
    dentists.push(dentist);
    localStorage.setItem("dentists", JSON.stringify(dentists));
    alert("Rhe dentist has been setted as favourite")
  };

  return (
    <div className={theme.className}>
      <div className="card">
        <Link to={"/detail/" + dent.id}>
          <h4>{dent.name}</h4>
        </Link>
        <img className="doctor" src="./images/doctor.jpg" alt="" />
        <h5>{dent.username}</h5>
        <p>ID {dent.id}</p>

        {/* En cada card deberan mostrar en name - username y el id */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button
          onClick={(e) => {
            addFav();
          }}
          className="favButton"
        >
          ‚≠ê
        </button>
      </div>
    </div>
  );
};

export default Card;
