import React, { useEffect, useState, useContext } from 'react'
import { useParams } from 'react-router-dom'
import { ContextGlobal } from "./utils/global.context";
//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {
 
  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  const { id } = useParams();
  const [dentist, setDentist] = useState(undefined);
  const { theme } = useContext(ContextGlobal);
  const isDarkMode = theme === "dark" || false; 
  
 
 
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setDentist(data);
       }); }, [id]);


  
  return (
    <>
      <h1>Detail Dentist {dentist?.name} </h1>
      <div className="detailDentist">
      <div className={`card ${isDarkMode ? "dark" : "light"}`}>
      <table>
        <div>
          <img src="/images/doctor.jpg" alt="doctor placeholder" />
        </div>
        <tbody>
        <tr>
          <td>Name: {dentist.name}</td>
          <td>Email: {dentist.email}</td>
          <td>Cel phone: {dentist.phone}</td>
          <td>Website: {dentist.website}</td>
        </tr>
        </tbody>
      </table>
      </div>
      </div>
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
