import React from "react";
import {useEffect} from "react";
import { Link } from "react-router-dom";
import { useContextGlobal } from "./utils/global.context";



const Card = ({ dentist}) => {
  
  const {favourites, setFavourites} = useContextGlobal()
  const{theme}= useContextGlobal()

  useEffect(() => {
    localStorage.setItem('favs', JSON.stringify(favourites))
  }, [favourites]);
  
  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    const dentists = JSON.parse(localStorage.getItem("dentists") || "[]");
    const dentist = {
      id: dentist.id,
      name: dentist.name,
      username: dentist.username,
    };
    dentists.push(dentist);
    localStorage.setItem("dentists", JSON.stringify(dentists));
    alert("The dentist it has been added to favs")
  };

  return (
    <div className="card" >
      <img src="./images/doctor.jpg" alt="doc" width='200px'/>
      
      <p>{<Link to={`/${id}`} className="dentistName" id={dentist.id}>{name}</Link>}</p>
      <p className="dentist" id={dentist.id}>{username} </p>
      

        {/* En cada card deberan mostrar en name - username y el id */}
        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}
        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton" id={changeTheme}>Add Fav</button>
    </div>
  );
};

export default Card;
